import fetch from "isomorphic-unfetch";

export default function LoadBook(props) {
  return <div>{props.data}</div>;
}

LoadBook.getInitialProps = async function() {
  const res = fetch("data/war and peace.txt");

  const data = res.text();

  return <div>{data}</div>;
};

