import Fetch from "isomorphic-unfetch"

export default function loadBook() {
  return <div>War and Peace</div>;
}

loadBook.getInitialProps = async function() {
  const res = fetch("http://www.gutenberg.org/files/2600/2600-0.txt").then(
    function(response) {
      return response;
    }
  );
  return res;
};

